'use strict';angular.module('app').factory('trans',['$rootScope','$q','$urlRouter','bridge','crud',function($rootScope,$q,$urlRouter,bridge,crud){return function(para){crud.$http({method:'GET',url:bridge.GLOBAL_CONFIG.path+'web/common?lang='+para,cache:true}).then(function(res){if(res){$rootScope.rootComm.dt=res.data.data}},function(err){console.log(err)})}}]);